mapper.select:

select count(*) from product where category1 REGEXP(#{criCategory1}) and category2 REGEXP(#{criCategory2}) and title REGEXP(#{criSerTitle})

and price >= #{minPrice}

and price <= #{maxPrice}

and stock >= #{minStock}

and stock <=#{maxStock}
order by ${order} desc

