choose.when:
select bd_num, user_id, title, content, writedate, reply, file1, group_num, step, indent, hit, bd_category1, bd_category2, bd_id, substr(updatedate,1,12) as updatedate,status 
		from (select ROWNUM as rnum, A.* from (select * from board where bd_category2= #{bd_category2} order by group_num desc, step, indent)A) 
		where rnum >= #{startPage} and rnum <= #{endPage} and (user_id Like #{serName} or Title Like #{serTitle} or Content Like #{serContent})

select bd_num, user_id, title, content, writedate, reply, file1, group_num, step, indent, hit, bd_category1, bd_category2, bd_id, substr(updatedate,1,12) as updatedate,status
		 from (select ROWNUM as rnum, A.* from (select * from board where bd_category2= #{bd_category2} and user_id =#{id} order by group_num desc, step, indent)A) 
		 where rnum >= #{startPage} and rnum <= #{endPage} and (user_id Like #{serName} or Title Like #{serTitle} or Content Like #{serContent})

choose.otherwise:
select bd_num, user_id, title, content, writedate, reply, file1, group_num, step, indent, hit, bd_category1, bd_category2, bd_id, substr(updatedate,1,12) as updatedate
		 from (select ROWNUM as rnum, A.* from (select * from board where bd_category2= #{bd_category2} order by group_num desc, step, indent)A) 
		 where rnum >= #{startPage} and rnum <= #{endPage} and (user_id Like #{serName} or Title Like #{serTitle} or Content Like #{serContent})
